mapper.insert:
INSERT INTO orders (
user_id, screening_id, seat_count,
price, status, create_time
) VALUES (
#{userId}, #{screeningId}, #{seatCount},
#{price}, #{status}, #{createTime}
)

mapper.select:
SELECT * FROM orders
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM orders
WHERE user_id = #{userId}

    AND status = #{status}

SELECT * FROM orders

    WHERE status = #{status}

ORDER BY create_time DESC
LIMIT #{offset}, #{size}
